require_once('Messages.php');

class MysqlConnect
{
    private static $instance;

    private $host, $username, $password, $databaseName;
    /**
     * @var PDO
     */
    private $conn;

    public static function instance()
    {
        if (!isset(self::$instance)) {
            self::$instance = new MysqlConnect();
        }
        return self::$instance;
    }

    public function __construct()
    {
        $this->host = Messages::DB_HOST;
        $this->username = Messages::DB_USERNAME;
        $this->password = Messages::DB_PASSWORD;
        $this->databaseName = Messages::DB_DATABASE;
    }

    private function connect()
    {
        try {
            $this->conn = new PDO("mysql:hos=$this->host;dbname=$this->databaseName", $this->username, $this->password);
            $this->conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);


        } catch (PDOException $e) {
            echo "Error: " . $e->getMessage();
        }
    }

    private function disconnect()
    {
        $this->conn = null;
    }

    public function executeSql($sql)
    {
        $this->connect();
        $this->conn->exec($sql);
        $this->disconnect();
    }

    public function insertUser($username, $email, $password, $ip)
    {
        $this->connect();
        $stmt = $this->conn->prepare("INSERT INTO `users`(`username`, `email`, `password`, `ip`) VALUES (:username, :email, :password, :ip)");
        $stmt->bindParam(':username', $username);
        $stmt->bindParam(':email', $email);
        $stmt->bindParam(':password', password_hash($password, PASSWORD_DEFAULT));
        $stmt->bindParam(':ip', $ip);

        $stmt->execute();


        $this->disconnect();
    }

    public function insertGroup($name, $avatar)
    {
        $this->connect();
        $stmt = $this->conn->prepare("INSERT INTO `groups`(`name`, `avatar`) VALUES (:altceva, :avatar)");
        $stmt->bindParam(':altceva', $name);
        $stmt->bindParam(':avatar', $avatar);

        $stmt->execute();
        $this->disconnect();
    }

    public function insertPermission($id, $PermArray)
    {
        $this->connect();


        $stmt = $this->conn->prepare(
            "INSERT INTO `permission-meta`(`permission`, `group_id`,`permission_value`) " .
            "VALUES (:val1, :id, :val2);"
        );

        $val1 = (int)$id;
        $stmt->bindParam(':id', $val1, PDO::PARAM_INT);
        $stmt->bindParam(':val1', $val2);
        $stmt->bindParam(':val2', $val3, PDO::PARAM_INT);


        foreach ($PermArray as $key => $value) {
            $val2 = $key;
            $val3 = (int)$value;
            $stmt->execute();
        }


        $this->disconnect();
    }

