    public function getParentAndChilds()
    {
        $sql = "SELECT DISTINCT `parent`.`id_parent`,`parent`.`description`,`parent`.`image`,`parent`.`name`\"parent\",`child`.`id_child`,`child`.`name`\"child\" FROM 
(SELECT `category_parent`.`id_parent`,`category`.`name`,`category`.`description`,`category`.`image` FROM `category_parent`,`category` WHERE `category_parent`.`id_parent` LIKE `category`.`id`) `parent`,
(SELECT `category_parent`.`id_child`,`category`.`name` FROM `category_parent`,`category` WHERE `category_parent`.`id_child` LIKE `category`.`id`) `child`,`category_parent`
WHERE 
    `parent`.`id_parent` LIKE `category_parent`.`id_parent` AND `child`.`id_child` LIKE `category_parent`.`id_child`
ORDER BY `parent`.`name`,`child`.`name`";

        $this->connect();

        $stmt = $this->conn->prepare($sql);
        $stmt->execute();
        $result = $stmt->fetchAll(PDO::FETCH_ASSOC);

        if (sizeof($result) > 0) {
            $this->disconnect();
            return $result;
        } else {
            $this->disconnect();
            return null;
        }

    }

