    public function checkPermission($permission,$id){
        $this->connect();

        $stmt = $this->conn->prepare("SELECT `permission-meta`.`permission_value` FROM `permission-meta`,`user_groups` WHERE `permission-meta`.`group_id` LIKE `user_groups`.`id_group` AND `user_groups`.`id_user` LIKE :id AND `permission-meta`.`permission` LIKE :permission");
        $stmt->bindParam(":id", $id,PDO::PARAM_INT);
        $stmt->bindParam(":permission", $permission);
        $stmt->execute();

        $result = $stmt->fetchAll(PDO::FETCH_ASSOC);

        if (sizeof($result) == 1 && $result[0]['permission_value'] == 1) {
            $this->disconnect();
            return true;
        } else {
            $this->disconnect();
            return false;

        }

    }

